sp_widget.client_script:
function() {
	/* widget controller */
	var c = this;
	c.selectItem = function(idx){
		var id = c.data.notes[idx].sys_id;
		console.log("Note ID: "+id);
		$rootScope.noteID = id;
		$rootScope.$emit('selectNote',id);
	}
}

sp_widget.script:
(function() {
  /* populate the 'data' object */
  /* e.g., data.table = $sp.getValue('table'); */
	data.notes = [];

	var gr = new GlideRecord('x_snc_createnotes_note');
	gr.addQuery('user.sys_id',gs.getUserID());
	gr.orderByDesc('sys_created_on');
	gr.query();
	while(gr.next()){
		/*var noteObj = {"number":"","title":"","sys_id":""};
		noteObj.number = gr.getDisplayValue("number");
		noteObj.title = gr.getDisplayValue("title");
		noteObj.sys_id = gr.getDisplayValue("sys_id");*/
		var noteObj = {};
		//Use service portal helper method to get some display values
		$sp.getRecordDisplayValues(noteObj,gr,"number,title,sys_id");

		noteObj.note = gr.getValue("note").slice(0,20);
		data.notes.push(noteObj);

	}

})();
